	if [ "$brik" = "1" -o "$brik" = "4" -o "$brik" = "7" ]; then		3dbucket -prefix ${submod}_Coef+orig -fbuc ${subcond1}_bucket+orig[1..7(3)]	elif [ "$brik" = "2" -o "$brik" = "5" -o "$brik" = "8" ]; then		3dbucket -prefix ${submod}_Tstat+orig -fbuc ${subcond1}_bucket+orig[2..8(2)]	elif [ "$brik" = "3" -o "$brik" = "6" -o "$brik" = "9" ]; then		3dbucket -prefix ${submod}_Fstat+orig -fbuc ${subcond1}_bucket+orig[3..9(3)]	else		# Note well that the Full_Fstat applies to the entire dataset, and not just the conditions		3dbucket -prefix ${subcond1}_Full_Fstat+orig -fbuc ${subcond1}_bucket+orig[0]	fifi####################################################################################################echo ""brik 0-1 Fstat_${factorA}_${mod}_${brik}brik  Fstat_${factorB}_${mod}_${brik}brik  Fstat_${factorA}_${mod}_${brik}